import React, { Component } from 'react'
import { connect } from 'react-redux'
import SearchInput from '../components/search/SearchInput'
import SearchResults from '../components/search/SearchResults'
import { addSearchResults, deleteSearchResults } from '../actions/searchActions'

class SearchContainer extends Component {

  // resetSearch = () => this.setState( { query: "", searchResults: [], errors: "" } )

  render() {
    const { searchResults, addSearchResults, deleteSearchResults } = this.props

    return (
      <div>
        <SearchInput addSearchResults={addSearchResults} deleteSearchResults={deleteSearchResults}/>
        <SearchResults songs={searchResults} />
      </div>
    )
  }
}

export default connect( ({searchResults}) => ({searchResults} ), { addSearchResults, deleteSearchResults, addSearchMessage } )(SearchContainer)
