import React from 'react';
import Song from './Song'

const Songs = ({ songs, match }) => {

    const renderList = () => {
        const playlistSongs = songs.filter( song => {
            return song.playlistIds.find( id => id === parseInt(match.params.playlistId, 10))
        })

        return playlistSongs.map( song => <ul><Song song={song} /></ul>)
    }

    return (
      <div>
        { songs.length > 0 ? renderList() : <div>Add a song</div> }
      </div>
    )

  }

  export default Songs;


