import React from 'react';
import { Link } from 'react-router-dom';

const Playlist = ({url, playlistIndex, playlists, deletePlaylist}) => {

    const handleClick = () => {
        deletePlaylist(playlists[playlistIndex].id)
    }

    return (
        <li>
            <Link key={playlistIndex} to={`${url}/${playlistIndex}/songs`}>{playlists[playlistIndex].name}</Link>
            <button onClick={handleClick} > X </button>
        </li>
    );
  }

export default Playlist;
