import React, { Component } from 'react'
import { Route } from 'react-router-dom'
import Holding from '../components/Holding'

import Home from '../components/registrations/Home'
import Login from '../components/registrations/Login'
import Signup from '../components/registrations/Signup'

class PlaylistsContainer extends Component {

  componentDidMount() { this.props.fetchUser() }
  handleLoading = () => this.props.user.loading ? this.tempPage() : this.authUser()

  authUser = () => {
    // Checks if user has logged in + :userId matches logged in user id
    // If :userId does not match logged in user id, logged in user id is reset in url and user is redirected to PlaylistContainer
    const isValidUser = this.props.user.id === parseInt(this.props.match.params.userId, 10)
    return isValidUser ? this.loadPage() : this.redirect()
  }

  redirect = () => this.props.history.push('/')
  tempPage = () => <Holding />

  loadPage = () => {
    const { fetchUser, user, loginUser } = this.props

    return (
        <div>
            <h1>Spotify Playlister</h1>
            <Route
                exact path={`${match.url}`}
                render={props => (
                <Home {...props} fetchUser={fetchUser} user={user} /> )}
            />
            <Route 
                exact path='/login'
                render={props => (
                <Login {...props} fetchUser={fetchUser} user={user} loginUser={loginUser} /> )}
            />
            <Route 
                exact path='/signup' 
                render={props => (
                <Signup {...props} fetchUser={fetchUser} user={user} loginUser={loginUser} /> )}
            />
        </div>
    )
  }
  render () { return <div>{ this.loadPage() }</div> }
}
