import { addPlaylist, resetPlaylistStore } from "./playlistActions"

export const loginUser = user => {
    return { type: "LOGIN_USER", user }
}

export const logoutUser = () => {
    return { type: "LOGOUT_USER" }
}

export const fetchUser = () => {
    return dispatch => {
        dispatch({type: 'LOADING_USER'});
        // Clear playlist store when user logs out or during multiple redirects (e.g., if user manually enters incorrect playlistId url param)
        dispatch(resetPlaylistStore())
        fetch('http://localhost:3001/logged_in', { credentials: 'include' })
        .then(response => response.json())
        .then(json => {
            debugger
            if (json.user.isLoggedIn) {
                for (const playlist of json.playlists) dispatch(addPlaylist(playlist))
                dispatch(loginUser(json.user))
            }
            debugger
        })
    }
}
