import React, { Component } from 'react'
import { Route } from 'react-router-dom'
import Holding from '../components/Holding'
import { connect } from 'react-redux'
import NoMatch from '../components/NoMatch'

class SongsContainer extends Component {

    // componentDidMount() {
    //     debugger
    //     // this.props.fetchUser()
    // }

    // handleLoading = () => {
    //     // if (this.props.user.loading) {
    //     //     return this.tempPage()
    //     // } else {
    //         return this.authPlaylist()
    //     // }
    // }

    authPlaylist = () => {
        // Check user has logged in + :userId matches logged in user id
        // If :userId does not match logged in user id, force correct user id in url and redirect PlaylistContainer
        const isValidPlaylist = this.props.playlists.find( e => e.id === parseInt(this.props.match.params.playlistId, 10) )
        return isValidPlaylist ? this.loadPage() : this.redirect()
    }

    redirect = () => this.props.history.push(`/users/${this.props.user.id}/playlists`)
    tempPage = () => <Holding />

    loadPage = () => {
        return (
            <div>
                <Route 
                exact path={`${this.props.match.url}`}
                render={ () => (
                    <div>Test</div>
                )}
                />
                <Route path='*' render={() => <NoMatch /> }/>
            </div>
        )
    }

    render () { return <div>{this.authPlaylist()}</div> }

};

export default connect( ({songs}) => ({songs}), {  } )(SongsContainer)
