import React from 'react';

const Song = ({playlistSong, match, deleteSong}) => {

    const handleClick = () => {



        const configObj = {
            method: 'DELETE',
            headers: {'Content-type': 'application/json; charset=UTF-8'},
            credentials: 'include'
        }
        debugger
        fetch(`http://localhost:3001${match.url}/${playlistSong.id}`, configObj)   
            .then(response => response.json())
            // TODO: Revise below code + destroy song in controller so that you are removing the playlist id from playlistIds/playlist_song and not actually removing the song
            .then(json => {
                if (!json.errors) {
                    deleteSong(playlistSong.id)
                } else {
                    alert(json.errors.join("\n"))
                } 
            })
            .catch(error => console.log('API errors:', error))
    }

    return (
        <div key={playlistSong.id}>
            <p>Song: <a target="_blank" rel="noopener noreferrer" href={playlistSong.url}>{playlistSong.title}</a></p>
            <p>Album: {playlistSong.album_name}</p>
            <p>Artists: {JSON.parse(playlistSong.artists).join(', ')}</p>
            <button onClick={handleClick} > X </button>
        </div>
    );
  }

export default Song;
