import SearchResults from './SearchResults'

class SongSearch extends Component {

    state = { query: "", results: [] }

    handleChange = (e) => this.setState({ query: e.target.value })
    handleSubmit = (e) => {
        e.preventDefault();        
        // addSong(e.target.value)
        this.setState({ query: '' })

    }    

