import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import { connect } from 'react-redux'

import PlaylistsContainer from '../containers/PlaylistsContainer'
import Index from '../components/registrations/Index'
import Login from '../components/registrations/Login'
import Signup from '../components/registrations/Signup'
import NoMatch from '../components/NoMatch'
import Logout from '../components/Logout'

class RegistrationsContainer extends Component {

  state = {
    isLoggedIn: false,
    user: {}
  }

  componentDidMount() {
    this.getLoginStatus()
  }

  getLoginStatus = () => {
    return (
      fetch('http://localhost:3001/logged_in', {
        credentials: 'include' // could also use 'same-origin'
      })
      .then(response => response.json())
      .then(json => {
        if (json.logged_in) {
          this.handleLogin(json)
        } else {
          this.handleLogout()
        }
      })
      .catch(error => console.log('API error:', error))
    )
  }

  handleLogin = (data) => {
    this.props.updateLoginInfo({
      isLoggedIn: true,
      user: data.user
    })
  }

  handleLogout = () => {
    console.log('logged out')
    this.props.updateLoginInfo({
      isLoggedIn: false,
      user: {}
    })
  }

  render() {
    return (
      <div>
         <Router>
          <Switch>
            <Route exact path='/' 
              render={props => (
              <Index {...props} getLoginStatus={this.getLoginStatus} loggedInStatus={this.state.isLoggedIn} user={this.props.loginInfo.user}/>
              )}
            />
            <Route 
              exact path='/login'
              render={props => (
              <Login {...props} handleLogin={this.handleLogin} getLoginStatus={this.getLoginStatus} loggedInStatus={this.state.isLoggedIn} user={this.state.user} />
              )}
            />
            <Route 
              exact path='/signup' 
              render={props => (
              <Signup {...props} handleLogin={this.handleLogin} getLoginStatus={this.getLoginStatus} loggedInStatus={this.state.isLoggedIn} user={this.state.user}/>
              )}
            />
            <Route
              exact path='/users/:user_id/playlists'
              render={props => (
                <PlaylistsContainer {...props} getLoginStatus={this.getLoginStatus} handleLogout={this.handleLogout} loggedInStatus={this.state.isLoggedIn} user={this.state.user}/>
              )}
            />
            <Route exact path='/logout' render={() => <Logout />} />
            <Route path='*' render={() => <NoMatch />} />
          </Switch>
        </Router>
      </div>
    );
  }
}

const mapStateToProps = ({loginInfo}) => ({loginInfo})
const mapDispatchToProps = dispatch => ({
  updateLoginInfo: loginInfo => dispatch({ type: "UPDATE_LOGIN_INFO", loginInfo })
})

export default connect(mapStateToProps, mapDispatchToProps)(RegistrationsContainer);
