import React, { useEffect } from 'react';
import { Link } from 'react-router-dom'

const Home = (props) => {

  useEffect (() => {
    props.getLoginStatus()
    .then( () => this.props.loggedInStatus ? redirect() : null )
  })

  const redirect = () => this.props.history.push(`/users/${this.props.user.id}/playlists`)

  return (
    <div>
      <Link to='/login'>Log In</Link>
      <br></br>
      <Link to='/signup'>Sign Up</Link>
    </div>
  );
};

export default Home;
